mapper.update:
UPDATE songpring_shop
SET title=#{title}, imagePath=#{imagePath}, genre=#{genre}, writer=#{writer}, publisher=#{publisher},
	regdate=#{regdate}, price=#{price}, content=#{content}, page=#{page}, bookSize=#{bookSize}
WHERE num=#{num}

mapper.select:
SELECT num, title, imagePath, genre, writer, publisher, TO_CHAR(regdate , 'YYYY-MM-DD') as regdate, price, content, sellCount, page, bookSize
FROM songpring_shop
WHERE num=#{num}

SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num, title, imagePath, genre, writer, publisher, TO_CHAR(regdate , 'YYYY-MM') as regdate, price, content, sellCount, page, bookSize FROM songpring_shop
WHERE title LIKE '%'||#{title}||'%'
WHERE title LIKE '%'||#{title}||'%' AND genre=#{genre}
WHERE genre=#{genre}
ORDER BY price ASC
ORDER BY price DESC
ORDER BY sellCount ASC
ORDER BY regdate ASC
ORDER BY num ASC
) result1) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

SELECT NVL(MAX(ROWNUM), 0)
FROM songpring_shop


			WHERE title LIKE '%'||#{title}||'%'


			WHERE title LIKE '%'||#{title}||'%'
			AND genre=#{genre}


			WHERE genre=#{genre}

mapper.update:
UPDATE songpring_shop
SET sellCount=sellCount+1
WHERE num=#{num}

mapper.delete:
delete SONGPRING_CART
where cartNum = #{cartNum}
and userId = #{userId}
